create table veeNaidud (
	id INT NOT NULL PRIMARY KEY IDENTITY(1,1),
	email VARCHAR(50),
	korterinumber VARCHAR(50),
	kuupaev DATE,
	külmVesi INT,
	soeVesi INT,
	makstud BIT
);

select 
email as '@email',
korterinumber,
kuupaev,
kulmVesi as 'vesi/kulmVesi',
soeVesi as 'vesi/soeVesi',
makstud
from veeNaidud for xml path('veeNaide'), root('veeNaidud')